<?include("function.inc.php");$keyword = $_GET['k'];$start = $_GET['s'];$limit = $_GET['l'];$images = get_images($keyword, $start, $limit);//var_dump($images);$temp_str = "";foreach($images as $item){	$id = $item['id'];	$image = $item['image_url'];	$title = $item['title'];	$temp_str .= '{"id":"'.$id.'", "image_url":"'.$image.'", "title":"'.$title.'"},';}if($temp_str != ''){	//echo 'not null';	$temp_str = substr($temp_str,0,strlen($temp_str)-1);	$temp_str = '['.$temp_str.']';}echo '{"ret":0,"msg":"ok","data":'.$temp_str.'}';?>