<?function conn_DB(){	$dbname = '';	$host = getenv('HTTP_BAE_ENV_ADDR_SQL_IP');	$port = getenv('HTTP_BAE_ENV_ADDR_SQL_PORT');	$user = getenv('HTTP_BAE_ENV_AK');	$pwd = getenv('HTTP_BAE_ENV_SK');	/*	$dbname = 'xiangce';	$host = "localhost";	$port = 3306;	$user = "root";	$pwd = "123456";	 */	/*接着调用mysql_connect()连接服务器*/	$link = @mysql_connect("{$host}:{$port}",$user,$pwd,true);	if(!$link) {		die("Connect Server Failed: " . mysql_error($link));	}		/*连接成功后立即调用mysql_select_db()选中需要连接的数据库*/	if(!mysql_select_db($dbname,$link)) {		die("Select Database Failed: " . mysql_error($link));	}	mysql_query("set character set 'utf8'");//读库	mysql_query("set names 'utf8'");//写库	return $link;}function get_images($k="", $s=0, $l=10){	$conn = conn_DB();	$condition = "";	$k=mysql_real_escape_string($k);	$s=(int)$s;	$l=(int)$l;	if($k){		$condition = " where title like '%". $k . "%' ";	}	$query_sql  = 'select * from images '. $condition  .' limit '. $s .',' . $l;	//echo $query_sql;	$ret = mysql_query($query_sql,$conn);	$result = array();	//var_dump($ret);	if($ret)	{		while($row = mysql_fetch_array($ret, MYSQL_ASSOC))		{			//var_dump($row);			$result[] = $row;		}	}	mysql_close($conn);	return $result;}?>